          { date: '2017-02', value: 0.148 },
          { date: '2018-01', value: 0.055 },
          { date: '2018-08', value: 0.045 },
        ],
        xField: 'date',
        yField: 'value',
        xAxis: false,
        yAxis: {
          line: null,
          grid: null,
          position: 'right',
          max: 0.16,
          tickCount: 8,
        },
        meta: {
          date: {
            sync: 'date',
          },
          value: {
            alias: '递增',
            formatter: (v) => `${(v * 100).toFixed(1)}%`,
          },
        },
        smooth: true,
        label: {
          callback: (value) => {
            return {
              offsetY: value === 0.148 ? 36 : value === 0.055 ? 0 : 20,
              style: {
                fill: '#1AAF8B',
                fontWeight: 700,
                stroke: '#fff',
                lineWidth: 1,
              },
            };
          },
        },
        color: '#1AAF8B',
      },
    },
  ],
});

plot.render();

